mapper.select:
select dm_id,dm_name,dm_email,dp_name
from dp_manager,department
where dp_manager.dp_id = department.dp_id and dp_manager.delete_time is null;

mapper.update:
update dp_manager

    update_time = current_timestamp
    ,dm_email = #{email}



         dm_id = #{id}
         dm_name = #{name}
         dm_id = -1

update dp_manager

    delete_time = current_timestamp



        dm_id = #{id}
        dm_name = #{name}
        dm_id = -1
